package years.days.day06;

import utils.FileToVector;

import java.io.IOException;
import java.util.Iterator;
import java.util.Vector;

public class Day6 {

    public static void main(String[] args) {

//String input = "nppdvjthqldpwncqszvftbrmjlhg";
            String input = "";

            int checkLength = 14;
            int endPoint = 14;
            boolean found = false;

            while (endPoint < input.length() && !found) {

                String check = input.substring(endPoint - checkLength, endPoint);
                found = true;
                for (int times = 0 ; times <checkLength; times++){
                    if (check.indexOf(check.substring(times,times+1)) != check.lastIndexOf(check.substring(times,times+1))) {
                        found = false;
                    }

                }
                endPoint++;
            }

            System.out.println("Found:"+(endPoint-1));



    }

}
